def colorCompare(col, epsilon):
	
	for r in ready:
		if col[0] > r[1][0] - epsilon and col[0] < r[1][0] + epsilon and\
		   col[1] > r[1][1] - epsilon and col[1] < r[1][1] + epsilon and\
		   col[2] > r[1][2] - epsilon and col[2] < r[1][2] + epsilon:
			return r[0]
	return None

